/* Main layout styles */
.split-pane {
    display: flex;
    height: calc(100vh - 56px);
    margin-top: 56px;
}

.file-tree {
    width: 300px;
    background-color: #f8f9fa;
    border-right: 1px solid #dee2e6;
    padding: 1rem;
    overflow-y: auto;
}

.visualization-pane {
    flex: 1;
    padding: 1rem;
    overflow-y: auto;
}

/* File upload area */
.upload-area {
    border: 2px dashed #dee2e6;
    border-radius: 5px;
    padding: 2rem;
    text-align: center;
    margin-bottom: 1rem;
    cursor: pointer;
    transition: border-color 0.3s ease;
}

.upload-area:hover {
    border-color: #0d6efd;
}

.upload-area.drag-over {
    border-color: #0d6efd;
    background-color: rgba(13, 110, 253, 0.1);
}

/* Code display */
.code-container {
    background-color: #f8f9fa;
    border-radius: 5px;
    padding: 1rem;
}

.code-block {
    font-family: 'Fira Code', monospace;
    font-size: 0.9rem;
    line-height: 1.5;
}

/* Graph visualization */
.graph-container {
    width: 100%;
    height: 600px;
    border: 1px solid #dee2e6;
    border-radius: 5px;
}

.node {
    cursor: pointer;
}

.node circle {
    fill: #fff;
    stroke: #0d6efd;
    stroke-width: 2px;
}

.node text {
    font-size: 12px;
    font-family: sans-serif;
}

.link {
    fill: none;
    stroke: #999;
    stroke-width: 1px;
}

/* Search functionality */
.search-container {
    margin-bottom: 1rem;
}

.search-results {
    max-height: 400px;
    overflow-y: auto;
}

.search-result-item {
    padding: 0.5rem;
    border-bottom: 1px solid #dee2e6;
    cursor: pointer;
}

.search-result-item:hover {
    background-color: #f8f9fa;
}

/* Loading spinner */
.spinner-container {
    display: flex;
    justify-content: center;
    align-items: center;
    height: 100%;
}

/* Error messages */
.error-message {
    color: #dc3545;
    padding: 0.5rem;
    margin: 0.5rem 0;
    border: 1px solid #dc3545;
    border-radius: 5px;
    background-color: rgba(220, 53, 69, 0.1);
}

// Main JavaScript functionality for code analysis visualizer

document.addEventListener('DOMContentLoaded', function() {
    // Initialize file upload handling
    initializeFileUpload();

    // Initialize code visualization
    initializeVisualization();

    // Initialize search functionality
    initializeSearch();
});

// File upload handling
function initializeFileUpload() {
    const uploadArea = document.querySelector('.upload-area');
    const fileInput = document.querySelector('#file-input');

    if (uploadArea && fileInput) {
        uploadArea.addEventListener('dragover', (e) => {
            e.preventDefault();
            uploadArea.classList.add('drag-over');
        });

        uploadArea.addEventListener('dragleave', () => {
            uploadArea.classList.remove('drag-over');
        });

        uploadArea.addEventListener('drop', (e) => {
            e.preventDefault();
            uploadArea.classList.remove('drag-over');
            const files = e.dataTransfer.files;
            handleFiles(files);
        });

        fileInput.addEventListener('change', (e) => {
            handleFiles(e.target.files);
        });
    }
}

async function handleFiles(files) {
    const formData = new FormData();

    for (const file of files) {
        if (!file.name.endsWith('.py')) {
            showError('Only Python files are allowed');
            continue;
        }
        formData.append('file', file);
    }

    try {
        showLoading();
        const response = await fetch('/upload', {
            method: 'POST',
            body: formData
        });

        const data = await response.json();

        if (!response.ok) {
            throw new Error(data.error || 'Upload failed');
        }

        updateFileTree(data);
        showSuccess('Files uploaded successfully');
    } catch (error) {
        showError(error.message);
    } finally {
        hideLoading();
    }
}

// Visualization handling
function initializeVisualization() {
    const graphContainer = document.querySelector('.graph-container');

    if (!graphContainer) return;

    const width = graphContainer.clientWidth;
    const height = graphContainer.clientHeight;

    const svg = d3.select(graphContainer)
        .append('svg')
        .attr('width', width)
        .attr('height', height);

    // Initialize zoom behavior
    const zoom = d3.zoom()
        .scaleExtent([0.1, 4])
        .on('zoom', (event) => {
            container.attr('transform', event.transform);
        });

    svg.call(zoom);

    const container = svg.append('g');
}

async function visualizeCode(fileId) {
    try {
        showLoading();
        const response = await fetch(`/visualize/${fileId}`);
        const data = await response.json();

        if (!response.ok) {
            throw new Error(data.error || 'Visualization failed');
        }

        renderGraph(data);
    } catch (error) {
        showError(error.message);
    } finally {
        hideLoading();
    }
}

function renderGraph(data) {
    const container = d3.select('.graph-container g');

    // Clear previous graph
    container.selectAll('*').remove();

    const simulation = d3.forceSimulation(data.nodes)
        .force('link', d3.forceLink(data.links).id(d => d.id))
        .force('charge', d3.forceManyBody().strength(-300))
        .force('center', d3.forceCenter(width / 2, height / 2));

    // Create links
    const links = container.append('g')
        .selectAll('line')
        .data(data.links)
        .enter()
        .append('line')
        .attr('class', 'link');

    // Create nodes
    const nodes = container.append('g')
        .selectAll('.node')
        .data(data.nodes)
        .enter()
        .append('g')
        .attr('class', 'node')
        .call(d3.drag()
            .on('start', dragstarted)
            .on('drag', dragged)
            .on('end', dragended));

    nodes.append('circle')
        .attr('r', 5);

    nodes.append('text')
        .attr('dx', 12)
        .attr('dy', '.35em')
        .text(d => d.name);

    simulation.on('tick', () => {
        links
            .attr('x1', d => d.source.x)
            .attr('y1', d => d.source.y)
            .attr('x2', d => d.target.x)
            .attr('y2', d => d.target.y);

        nodes
            .attr('transform', d => `translate(${d.x},${d.y})`);
    });
}

// Search functionality
function initializeSearch() {
    const searchInput = document.querySelector('#search-input');

    if (searchInput) {
        let timeout = null;

        searchInput.addEventListener('input', (e) => {
            clearTimeout(timeout);
            timeout = setTimeout(() => {
                performSearch(e.target.value);
            }, 300);
        });
    }
}

async function performSearch(query) {
    if (!query) {
        clearSearchResults();
        return;
    }

    try {
        const response = await fetch('/search', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({ query })
        });

        const data = await response.json();

        if (!response.ok) {
            throw new Error(data.error || 'Search failed');
        }

        displaySearchResults(data);
    } catch (error) {
        showError(error.message);
    }
}

// Utility functions
function showLoading() {
    const spinner = document.querySelector('.spinner-container');
    if (spinner) spinner.style.display = 'flex';
}

function hideLoading() {
    const spinner = document.querySelector('.spinner-container');
    if (spinner) spinner.style.display = 'none';
}

function showError(message) {
    const errorContainer = document.createElement('div');
    errorContainer.className = 'error-message';
    errorContainer.textContent = message;
    document.querySelector('main').prepend(errorContainer);
    setTimeout(() => errorContainer.remove(), 5000);
}

function showSuccess(message) {
    const successContainer = document.createElement('div');
    successContainer.className = 'alert alert-success';
    successContainer.textContent = message;
    document.querySelector('main').prepend(successContainer);
    setTimeout(() => successContainer.remove(), 5000);
}

// Drag functions for graph nodes
function dragstarted(event) {
    if (!event.active) simulation.alphaTarget(0.3).restart();
    event.subject.fx = event.subject.x;
    event.subject.fy = event.subject.y;
}

function dragged(event) {
    event.subject.fx = event.x;
    event.subject.fy = event.y;
}

function dragended(event) {
    if (!event.active) simulation.alphaTarget(0);
    event.subject.fx = null;
    event.subject.fy = null;
}

// D3.js visualization functionality
class CodeVisualization {
    constructor(containerId) {
        this.container = d3.select(`#${containerId}`);
        this.width = this.container.node().getBoundingClientRect().width;
        this.height = this.container.node().getBoundingClientRect().height;
        this.initialize();
    }

    initialize() {
        // Create SVG container
        this.svg = this.container.append('svg')
            .attr('width', this.width)
            .attr('height', this.height);

        // Create force simulation
        this.simulation = d3.forceSimulation()
            .force('link', d3.forceLink().id(d => d.id))
            .force('charge', d3.forceManyBody().strength(-300))
            .force('center', d3.forceCenter(this.width / 2, this.height / 2));

        // Initialize groups for links and nodes
        this.linksGroup = this.svg.append('g').attr('class', 'links');
        this.nodesGroup = this.svg.append('g').attr('class', 'nodes');

        // Add zoom behavior
        const zoom = d3.zoom()
            .scaleExtent([0.1, 4])
            .on('zoom', (event) => {
                this.svg.selectAll('g').attr('transform', event.transform);
            });

        this.svg.call(zoom);
    }

    update(data) {
        // Clear existing elements
        this.linksGroup.selectAll('*').remove();
        this.nodesGroup.selectAll('*').remove();

        // Create links
        const links = this.linksGroup
            .selectAll('line')
            .data(data.links)
            .enter()
            .append('line')
            .attr('stroke', '#999')
            .attr('stroke-opacity', 0.6)
            .attr('stroke-width', d => Math.sqrt(d.value));

        // Create nodes
        const nodes = this.nodesGroup
            .selectAll('circle')
            .data(data.nodes)
            .enter()
            .append('g')
            .attr('class', 'node')
            .call(d3.drag()
                .on('start', this.dragstarted.bind(this))
                .on('drag', this.dragged.bind(this))
                .on('end', this.dragended.bind(this)));

        // Add circles to nodes
        nodes.append('circle')
            .attr('r', 5)
            .attr('fill', d => this.getNodeColor(d.type));

        // Add labels to nodes
        nodes.append('text')
            .attr('dx', 12)
            .attr('dy', '.35em')
            .text(d => d.name)
            .attr('font-size', '12px');

        // Update simulation
        this.simulation
            .nodes(data.nodes)
            .on('tick', () => {
                links
                    .attr('x1', d => d.source.x)
                    .attr('y1', d => d.source.y)
                    .attr('x2', d => d.target.x)
                    .attr('y2', d => d.target.y);

                nodes
                    .attr('transform', d => `translate(${d.x},${d.y})`);
            });

        this.simulation.force('link')
            .links(data.links);

        // Restart simulation
        this.simulation.alpha(1).restart();
    }

    getNodeColor(type) {
        const colors = {
            'function': '#1f77b4',
            'class': '#2ca02c',
            'module': '#ff7f0e'
        };
        return colors[type] || '#999';
    }

    dragstarted(event, d) {
        if (!event.active) this.simulation.alphaTarget(0.3).restart();
        d.fx = d.x;
        d.fy = d.y;
    }

    dragged(event, d) {
        d.fx = event.x;
        d.fy = event.y;
    }

    dragended(event, d) {
        if (!event.active) this.simulation.alphaTarget(0);
        d.fx = null;
        d.fy = null;
    }
}

// Initialize visualization when the document is loaded
document.addEventListener('DOMContentLoaded', function() {
    window.codeViz = new CodeVisualization('graph-container');
});
